SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_tipo_item ( ie_tipo_item_p text, nr_seq_tipo_lancamento_p bigint, nr_seq_tipo_acrescimo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);


BEGIN

select	substr(ds_valor_dominio,1,254)
into STRICT	ds_retorno_w
from	valor_dominio
where	cd_dominio	= 1930
and	vl_dominio	= ie_tipo_item_p;

if (ie_tipo_item_p	= '20') then
	begin
	select	ds_retorno_w || ' - ' || ds_tipo_lancamento
	into STRICT	ds_retorno_w
	from	pls_tipo_lanc_adic
	where	nr_sequencia	= nr_seq_tipo_lancamento_p;
	exception
	when others then
		ds_retorno_w	:= ds_retorno_w;
	end;
elsif (ie_tipo_item_p	= '22') then
	begin
	select	ds_retorno_w || ' - ' || ds_acrescimo
	into STRICT	ds_retorno_w
	from	pls_tipo_acrescimo
	where	nr_sequencia	= nr_seq_tipo_acrescimo_p;
	exception
	when others then
		ds_retorno_w	:= ds_retorno_w;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_tipo_item ( ie_tipo_item_p text, nr_seq_tipo_lancamento_p bigint, nr_seq_tipo_acrescimo_p bigint) FROM PUBLIC;

