CREATE OR REPLACE FUNCTION pls_obter_tipo_material_abrev ( ie_tipo_material_p text) RETURNS varchar AS $body$
DECLARE

/*
1 - Material
2 - Medicamento Comercial
3 - Medicamento Genérico
4 - Serviços
5 - Gêneros Alimentícios
6 - Medicamento sem apresentação
7 - Material da bolsa de sangue
*/
ds_retorno_w			varchar(255);


BEGIN

if (ie_tipo_material_p	= '1') or (ie_tipo_material_p	= '7') then
	ds_retorno_w	:= 'MAT';
elsif (ie_tipo_material_p	= '2') or (ie_tipo_material_p	= '3') or (ie_tipo_material_p	= '6') then
	ds_retorno_w	:= 'MED';
elsif (ie_tipo_material_p	= '4') then
	ds_retorno_w	:= 'SERV';
elsif (ie_tipo_material_p	= '5') then
	ds_retorno_w	:= 'GA';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
