CREATE OR REPLACE FUNCTION pls_obter_tipo_prest_prod ( nr_seq_prestador_p bigint, nr_seq_plano_p bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter os tipos do prestador que estão vinculados a um produto 'OPS - Prestador > Prestadores > Produtos/SCA' se não houver cadastros trará todos os tipos
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/cd_estabelecimento_w		smallint;
ds_retorno_w			varchar(4000)	:= null;
ds_tipo_prestador_w		varchar(255);

C01 CURSOR FOR
	SELECT	ds_tipo_prestador
	from (	SELECT	a.ds_tipo_prestador
		from	pls_prestador_tipo	b,
			pls_tipo_prestador	a
		where	a.nr_sequencia		= b.nr_seq_tipo
		and	b.nr_seq_prestador	= nr_seq_prestador_p
		and exists (	select	1
				from	pls_prestador_plano	x
				where	x.nr_seq_prestador	= nr_seq_prestador_p
				and	x.nr_seq_plano		= nr_seq_plano_p
				and exists (	select	1
						from	pls_prest_plano_tipo_prest	y
						where	x.nr_sequencia			= y.nr_seq_prest_plano
						and	y.nr_seq_tipo			= a.nr_sequencia))
		
union all

		select	a.ds_tipo_prestador
		from	pls_prestador_tipo	b,
			pls_tipo_prestador	a
		where	a.nr_sequencia		= b.nr_seq_tipo
		and	b.nr_seq_prestador	= nr_seq_prestador_p
		and exists (	select	1
				from	pls_prestador_plano	x
				where	x.nr_seq_prestador	= nr_seq_prestador_p
				and	x.nr_seq_plano		= nr_seq_plano_p
				and not exists (	select	1
						from	pls_prest_plano_tipo_prest	y
						where	x.nr_sequencia			= y.nr_seq_prest_plano))) alias4
	group by ds_tipo_prestador
	order by ds_tipo_prestador;

BEGIN

open C01;
loop
fetch C01 into
	ds_tipo_prestador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		ds_retorno_w	:= substr(ds_retorno_w || ', ' || ds_tipo_prestador_w,1,4000);
	else
		ds_retorno_w	:= substr(ds_tipo_prestador_w,1,4000);
	end if;
	end;
end loop;
close C01;

ds_retorno_w	:= substr(ds_retorno_w,1,4000);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
