SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_tipo_processo_web ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter o tipo de processo da guia
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_seq_segurado_w	bigint;
ie_tipo_processo_w	varchar(4)	:= 'P';
ie_segurado_inter_w	varchar(4);
ie_tipo_segurado_w	varchar(255);
ie_tipo_congenere_w	varchar(2);
nr_seq_congenere_w	bigint;
nr_seq_intercambio_w	pls_intercambio.nr_sequencia%type;
ie_tipo_contrato_w	pls_intercambio.ie_tipo_contrato%type;
qt_registros_ww		integer;
nr_seq_congenere_sup_w	pls_congenere.nr_seq_congenere%type;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	ie_segurado_inter_w := pls_obter_se_segurado_intercam(nr_seq_guia_p,null,null);

	if ((ie_segurado_inter_w IS NOT NULL AND ie_segurado_inter_w::text <> '') and ie_segurado_inter_w = 'S') then
		ie_tipo_processo_w := 'I';
	end if;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	begin
		select	nr_seq_segurado
		into STRICT	nr_seq_segurado_w
		from	pls_requisicao
		where	nr_sequencia = nr_seq_requisicao_p;
	exception
	when others then
		nr_seq_segurado_w	:= 0;
	end;

	begin
		select	ie_tipo_segurado,
			nr_seq_congenere,
			nr_seq_intercambio
		into STRICT	ie_tipo_segurado_w,
			nr_seq_congenere_w,
			nr_seq_intercambio_w
		from	pls_segurado
		where	nr_sequencia	= nr_seq_segurado_w;
	exception
	when others then
		ie_tipo_segurado_w	:= 'X';
		nr_seq_congenere_w	:= 0;
		nr_seq_intercambio_w	:= 0;
	end;

	if (coalesce(ie_tipo_segurado_w,'X') in ('I','T','H')) then
		begin
			select	ie_tipo_contrato
			into STRICT	ie_tipo_contrato_w
			from	pls_intercambio
			where	nr_sequencia	= nr_seq_intercambio_w;
		exception
		when others then
			ie_tipo_contrato_w	:= 'X';
		end;

		begin
			select	ie_tipo_congenere,
				nr_seq_congenere
			into STRICT	ie_tipo_congenere_w,
				nr_seq_congenere_sup_w
			from	pls_congenere
			where	nr_sequencia = nr_seq_congenere_w;
		exception
		when others then
			ie_tipo_congenere_w	:= 'X';
			nr_seq_congenere_sup_w	:= 0;
		end;

		select	count(1)
		into STRICT	qt_registros_ww
		from	pls_regra_envio_fundac_scs
		where (coalesce(ie_superior,'N') = 'S' and nr_seq_congenere	= nr_seq_congenere_sup_w);

		if ((coalesce(ie_tipo_congenere_w,'X') = 'OP') or ((coalesce(ie_tipo_segurado_w,'X') = 'T') and (pls_obter_se_env_scs_benef_rep(nr_seq_segurado_w) = 'N'))) then
			ie_tipo_processo_w := 'P';
		else
			ie_tipo_processo_w := 'I';
		end if;

		if (coalesce(ie_tipo_segurado_w,'X') = 'T' and (coalesce(ie_tipo_contrato_w,'X') = 'F') and qt_registros_ww > 0) then
			ie_tipo_processo_w := 'I';
		end if;
	end if;
end if;

return	ie_tipo_processo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_tipo_processo_web ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, cd_estabelecimento_p bigint) FROM PUBLIC;

