SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_tipo_protocolo_conta ( nr_seq_protocolo_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o tipo de protocolo da conta
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/*
ie_tipo_retorno_p
A - Abreviado
E - Extenso
*/
ie_tipo_protocolo_w	varchar(2);
ds_retorno_w		varchar(255);


BEGIN
	begin
		select	ie_tipo_protocolo
		into STRICT	ie_tipo_protocolo_w
		from	pls_protocolo_conta
		where	nr_sequencia	= nr_seq_protocolo_p;
	exception
	when others then
		ie_tipo_protocolo_w := '';
	end;

if (ie_tipo_retorno_p	= 'A') then
	ds_retorno_w	:= ie_tipo_protocolo_w;
elsif (ie_tipo_retorno_p	= 'E') then
	begin
		select	ds_valor_dominio
		into STRICT	ds_retorno_w
		from	valor_dominio
		where	cd_dominio = 3648
		and	ie_situacao = 'A'
		and	vl_dominio = ie_tipo_protocolo_w;
	exception
	when others then
		ds_retorno_w := '';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
