CREATE OR REPLACE FUNCTION pls_obter_tipo_seg_regra ( qt_vidas_p bigint, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w		bigint;
ds_retorno_w		varchar(2);


BEGIN

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_regra_w
	from	pls_regra_desconto
	where	ie_situacao		= 'A'
	and	pls_obter_se_desc_simulacao(nr_sequencia) = 'S'
	and (coalesce(cd_estabelecimento_p::text, '') = ''
	or	cd_estabelecimento	= cd_estabelecimento_p);
	
	if (	(nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') and nr_seq_regra_w <> 0 ) then	
		begin
		select	ie_tipo_segurado
		into STRICT	ds_retorno_w
		from	pls_preco_regra
		where	nr_seq_regra    = nr_seq_regra_w
		and	qt_vidas_p	>= qt_min_vidas
		and	qt_vidas_p	<= qt_max_vidas;		
		exception
			when others then
			ds_retorno_w	:= 'E';
		end;
	end if;

return	ds_retorno_w;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
