CREATE OR REPLACE FUNCTION pls_obter_tipo_tabela_preco ( nr_seq_regra_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

				 
/*	IE_OPÇÃO_P 
	 
	P Procedimento 
	M Material 
	S Servico 
	 
	DS_RETORNO_W 
	 
	C        Contrato 
	CG       Congênere 
	CO       Coparticipaç 
	I        Intercâmbio 
	O        Operadora 
	P        Prestador 
	R        Reembolso		 
*/
 
 
ds_retorno_w			varchar(255)	:= '';


BEGIN 
 
If (ie_opcao_p = 'P') then 
 
	select	ie_tipo_tabela 
	into STRICT	ds_retorno_w 
	from	pls_regra_preco_proc 
	where	nr_sequencia = nr_seq_regra_p 
	and	cd_estabelecimento = cd_estabelecimento_p;
	 
elsif (ie_opcao_p = 'M') then 
	 
	select	ie_tipo_tabela 
	into STRICT	ds_retorno_w 
	from	pls_regra_preco_mat 
	where	nr_sequencia = nr_seq_regra_p 
	and	cd_estabelecimento = cd_estabelecimento_p;
 
elsif (ie_opcao_p = 'M') then 
	 
	select	ie_tipo_tabela 
	into STRICT	ds_retorno_w 
	from	pls_regra_preco_servico 
	where	nr_sequencia = nr_seq_regra_p 
	and	cd_estabelecimento = cd_estabelecimento_p;
 
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
