CREATE OR REPLACE FUNCTION pls_obter_tipo_trans_intercamb ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2);
qt_pedido_autor_w		bigint;
qt_pedido_compl_w		bigint;


BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	count(1)
	into STRICT	qt_pedido_autor_w
	from	ptu_pedido_autorizacao
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

	select	count(1)
	into STRICT	qt_pedido_compl_w
	from	ptu_pedido_compl_aut
	where	nr_seq_requisicao	= nr_seq_requisicao_p;
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	count(1)
	into STRICT	qt_pedido_autor_w
	from	ptu_pedido_autorizacao
	where	nr_seq_guia	= nr_seq_guia_p;

	select	count(1)
	into STRICT	qt_pedido_compl_w
	from	ptu_pedido_compl_aut
	where	nr_seq_guia	= nr_seq_guia_p;
end if;

if (qt_pedido_autor_w	> 0) then
	ds_retorno_w	:= 'PA';
elsif (qt_pedido_compl_w	> 0) then
	ds_retorno_w	:= 'PC';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
