SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_titulos_contest_disc (nr_seq_lote_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_titulo_w		bigint;
qt_registro_w		bigint := 0;
nr_seq_fatura_w		bigint;
nr_seq_lote_contest_w	bigint;


BEGIN
/* CONTESTACAO */

if (upper(ie_opcao_p) = 'RF') then -- Receber fatura
	select	max(nr_seq_pls_fatura)
	into STRICT	nr_seq_fatura_w
	from	pls_lote_contestacao
	where	nr_sequencia = nr_seq_lote_p;

	if (nr_seq_fatura_w IS NOT NULL AND nr_seq_fatura_w::text <> '') then
		select	max(a.nr_titulo)
		into STRICT	nr_titulo_w
		from	titulo_receber	b,
			pls_fatura	a
		where	a.nr_titulo	= b.nr_titulo
		and	b.ie_situacao	<> '3'
		and	a.nr_sequencia	= nr_seq_fatura_w;
	end if;

elsif (upper(ie_opcao_p) = 'PF') then -- Pagar fatura
	select	max(nr_seq_ptu_fatura)
	into STRICT	nr_seq_fatura_w
	from	pls_lote_contestacao
	where	nr_sequencia = nr_seq_lote_p;

	if (nr_seq_fatura_w IS NOT NULL AND nr_seq_fatura_w::text <> '') then
		select	max(a.nr_titulo)
		into STRICT	nr_titulo_w
		from	titulo_pagar	b,
			ptu_fatura	a
		where	a.nr_titulo	= b.nr_titulo
		and	b.ie_situacao	<> '3'
		and	a.nr_sequencia	= nr_seq_fatura_w;
	end if;

elsif (upper(ie_opcao_p) = 'RGC') then -- Receber gerado contestação
	select	max(nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber
	where	nr_seq_pls_lote_contest = nr_seq_lote_p
	and	ie_situacao	<> '3';

	select	count(*)
	into STRICT	qt_registro_w
	from	pls_lote_discussao
	where	nr_seq_lote_contest	= nr_seq_lote_p
	and	nr_titulo_receber	= nr_titulo_w;

	if (qt_registro_w > 0) then
		nr_titulo_w := null;
	end if;

elsif (upper(ie_opcao_p) = 'PGD') then -- Pagar gerado discussão (Nº NDC externo)
	/* Só vai existir quando for uma contestação de faturamento */

	select	max(a.nr_titulo_pagar)
	into STRICT	nr_titulo_w
	from	titulo_pagar		b,
		pls_lote_discussao 	a
	where	a.nr_titulo_pagar	= b.nr_titulo
	and	b.ie_situacao		<> '3'
	and	a.nr_seq_lote_contest   = nr_seq_lote_p
	and	a.ie_primeira_discussao = 'S';
end if;

return	nr_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_titulos_contest_disc (nr_seq_lote_p bigint, ie_opcao_p text) FROM PUBLIC;

