SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_titulo_camara_comp ( nr_seq_lote_camara_p bigint, ie_tipo_titulo_p text) RETURNS bigint AS $body$
DECLARE

/*
ie_tipo_titulo_p
'R' - Título a Receber
'P' - Título a pagar
*/
nr_titulo_w		bigint;


BEGIN

if (ie_tipo_titulo_p = 'R') then
	select	max(a.nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber a
	where	a.nr_seq_pls_lote_camara	= nr_seq_lote_camara_p
	and	a.ie_situacao	<> '3';
elsif (ie_tipo_titulo_p = 'P') then
	select	max(a.nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_pagar a
	where	a.nr_seq_pls_lote_camara	= nr_seq_lote_camara_p
	and	a.ie_situacao	<> 'C';
end if;

return	nr_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
