CREATE OR REPLACE FUNCTION pls_obter_titulo_mensalidade ( nr_seq_mensalidade_p bigint, nr_seq_mens_segurado_p bigint) RETURNS bigint AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter o número do título da mensalidade
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_titulo_w	bigint;


BEGIN

if (nr_seq_mens_segurado_p IS NOT NULL AND nr_seq_mens_segurado_p::text <> '') then
	select	max(nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber 			a,
		pls_mensalidade_segurado 	b
	where	a.nr_seq_mensalidade	= b.nr_seq_mensalidade
	and	b.nr_sequencia		= nr_seq_mens_segurado_p
	and	a.ie_situacao		<> '3';
elsif (nr_seq_mensalidade_p IS NOT NULL AND nr_seq_mensalidade_p::text <> '') then
	select	max(nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber
	where	nr_seq_mensalidade	= nr_seq_mensalidade_p
	and	coalesce(nr_seq_mens_segurado::text, '') = ''
	and	ie_situacao		<> '3';
end if;

return nr_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_titulo_mensalidade ( nr_seq_mensalidade_p bigint, nr_seq_mens_segurado_p bigint) FROM PUBLIC;

