CREATE OR REPLACE FUNCTION pls_obter_titulo_repasse_emp ( nr_seq_lote_repasse_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_titulo_w	bigint;

C01 CURSOR FOR
	SELECT	nr_titulo
	from	titulo_receber
	where	nr_seq_lote_empresa = nr_seq_lote_repasse_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := nr_titulo_w;
	else
		ds_retorno_w	:= ds_retorno_w || ', ' || nr_titulo_w;
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
