CREATE OR REPLACE FUNCTION pls_obter_titulo_retencao_trib (nr_seq_imposto_p bigint) RETURNS bigint AS $body$
DECLARE


nr_titulo_w		bigint;


BEGIN
select	coalesce(max(c.nr_titulo),0)
into STRICT	nr_titulo_w
from	pls_lote_ret_trib_valor	c,
	pls_pag_prest_venc_trib	b,
	titulo_pagar		a
where	c.nr_sequencia		= b.nr_seq_lote_ret_trib_valor
and	a.nr_seq_pls_venc_trib	= b.nr_sequencia
and	a.nr_seq_tributo	= nr_seq_imposto_p;

return	nr_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
