CREATE OR REPLACE FUNCTION pls_obter_titulo_tributo (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w			bigint;
nr_seq_lote_w			bigint;
cd_tributo_w			bigint;


BEGIN

select	nr_titulo_pagar
into STRICT	nr_retorno_w
from	pls_lote_prot_venc_trib
where	nr_sequencia = nr_sequencia_p;

if (coalesce(nr_retorno_w::text, '') = '') then
	select	c.nr_sequencia,
		a.cd_tributo
	into STRICT	nr_seq_lote_w,
		cd_tributo_w
	from	pls_lote_prot_venc_trib	a,
		pls_lote_protocolo_venc	b,
		pls_lote_protocolo	c
	where	a.nr_seq_lote_venc	= b.nr_sequencia
	and	b.nr_seq_lote		= c.nr_sequencia
	and	a.nr_sequencia		= nr_sequencia_p;

	select	max(c.nr_titulo)
	into STRICT	nr_retorno_w
	from	titulo_pagar c,
		titulo_pagar b,
		titulo_pagar_imposto a
	where	a.nr_titulo		= b.nr_titulo
	and	a.nr_sequencia		= c.nr_seq_tributo
	and	b.nr_seq_lote_res_pls	= nr_seq_lote_w
	and	a.cd_tributo		= cd_tributo_w;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_titulo_tributo (nr_sequencia_p bigint) FROM PUBLIC;

