CREATE OR REPLACE FUNCTION pls_obter_total_ausencia_atend ( nr_seq_operador_p bigint, dt_inicio_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_retorno		double precision;


BEGIN

select	sum(round((dt_fim - dt_inicio) * 1440,2))
into STRICT	nr_retorno
from	pls_atendimento_ausencia
where	nr_seq_operador = nr_seq_operador_p
and	Trunc(dt_inicio) = dt_inicio_p
group by to_char(dt_inicio,'dd/mm/yyyy');

return	nr_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
