CREATE OR REPLACE FUNCTION pls_obter_tot_conta_glosa (nr_seq_protocolo_p bigint, nr_seq_motivo_glosa_p bigint) RETURNS bigint AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_registros_w
from 	pls_conta_glosa a,
		pls_conta b
where	a.nr_seq_conta = b.nr_sequencia
and		b.nr_seq_protocolo	= nr_seq_protocolo_p
and 	a.nr_seq_motivo_glosa	= nr_seq_motivo_glosa_p;

if (qt_registros_w = 0) then

	select	count(*)
	into STRICT	qt_registros_w
	from (
		SELECT 	count(*)
		from	pls_conta_glosa a,
			pls_conta b,
			pls_conta_proc c
		where	a.nr_seq_conta_proc	= c.nr_sequencia
		and	b.nr_seq_protocolo	= nr_seq_protocolo_p
		and 	a.nr_seq_motivo_glosa	= nr_seq_motivo_glosa_p
		and	b.nr_sequencia		= c.nr_seq_conta
		
union

		SELECT 	count(*)
		from	pls_conta_glosa a,
			pls_conta b,
			pls_conta_mat c
		where	a.nr_seq_conta_mat	= c.nr_sequencia
		and	b.nr_seq_protocolo	= nr_seq_protocolo_p
		and 	a.nr_seq_motivo_glosa	= nr_seq_motivo_glosa_p
		and	b.nr_sequencia		= c.nr_seq_conta) alias4;
end if;

return qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
