SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_tp_acomodacao_guia ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o tipo de acomodação da autorização
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w				varchar(50);
nr_seq_tipo_acomodacao_w		pls_guia_plano.nr_seq_tipo_acomodacao%type;
ie_tipo_acomodacao_w			pls_tipo_acomodacao.ie_tipo_acomodacao%type;


BEGIN
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then

	select	max(nr_seq_tipo_acomodacao)
	into STRICT	nr_seq_tipo_acomodacao_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;

	if (nr_seq_tipo_acomodacao_w IS NOT NULL AND nr_seq_tipo_acomodacao_w::text <> '') then

		select	max(ie_tipo_acomodacao)
		into STRICT	ie_tipo_acomodacao_w
		from	pls_tipo_acomodacao
		where	nr_sequencia = nr_seq_tipo_acomodacao_w;
	end if;
end if;

if (coalesce(ie_tipo_acomodacao_w,'X') = 'C') then
	ds_retorno_w := 'Coletivo';
elsif (coalesce(ie_tipo_acomodacao_w,'X') = 'I') then
	ds_retorno_w := 'Individual';
else
	ds_retorno_w := '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
