SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_tp_conta_prog ( nr_seq_conta_p pls_conta.nr_sequencia%type, dt_conta_p pls_conta.dt_atendimento_referencia%type) RETURNS PLS_CONTA.NR_SEQ_TIPO_CONTA%TYPE AS $body$
DECLARE


				
nr_seq_tipo_conta_w	pls_conta.nr_seq_tipo_conta%type := null;				
nr_seq_mprev_programa_w	pls_requisicao.nr_seq_mprev_programa%type;		
		

BEGIN

	select  max(c.nr_seq_mprev_programa)
	into STRICT	nr_seq_mprev_programa_w
	from   pls_execucao_requisicao a,
	       pls_guia_plano b,
	       pls_requisicao c,
	       pls_conta d
	where a.nr_seq_guia = b.nr_sequencia
	and a.nr_seq_requisicao = c.nr_sequencia
	and d.nr_seq_guia = b.nr_sequencia
	and d.nr_sequencia = nr_seq_conta_p;
	
	if (nr_seq_mprev_programa_w IS NOT NULL AND nr_seq_mprev_programa_w::text <> '') then
	
		select 	max(b.nr_sequencia)
		into STRICT	nr_seq_tipo_conta_w
		from	pls_tipo_conta_programa a,
			pls_tipo_conta b
		where 	a.nr_seq_tipo_conta = b.nr_sequencia
		and 	a.nr_seq_mprev_programa = nr_seq_mprev_programa_w
		and 	dt_conta_p between a.dt_ini_vigencia_ref and a.dt_fim_vigencia_ref
		and 	b.ie_situacao = 'A';
	
	end if;

return	nr_seq_tipo_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
