CREATE OR REPLACE FUNCTION pls_obter_transacao_compl_ptu ( nr_seq_auditoria_p bigint) RETURNS varchar AS $body$
DECLARE


nr_retorno_w			varchar(10);
nr_seq_req_proc_w		bigint;
nr_seq_req_mat_w		bigint;
nr_seq_compl_ptu_w		bigint;

C01 CURSOR FOR
	SELECT	coalesce(nr_seq_proc_origem,0),
		coalesce(nr_seq_mat_origem,0)
	from	pls_auditoria_item
	where	nr_seq_auditoria	= nr_seq_auditoria_p;

C02 CURSOR FOR
	SELECT	nr_seq_compl_ptu
	from	pls_requisicao_proc
	where	nr_sequencia	= nr_seq_req_proc_w
	
union

	SELECT	nr_seq_compl_ptu
	from	pls_requisicao_mat
	where	nr_sequencia	= nr_seq_req_mat_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_req_proc_w,
	nr_seq_req_mat_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_compl_ptu_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;
	end;
end loop;
close C01;

nr_retorno_w	:= coalesce(nr_seq_compl_ptu_w,0);

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
