CREATE OR REPLACE FUNCTION pls_obter_tributo_prestador ( nr_seq_prestador_p bigint, dt_mes_referencia_p timestamp, ie_tipo_tributo_p bigint) RETURNS bigint AS $body$
DECLARE


/* 	IE_TIPO_TRIBUTO_P
	1 -Tributo
	2 - Base calculo
*/
vl_tributo_w			double precision	:= 0;
vl_base_calculo_w		double precision	:= 0;
vl_retorno_w			double precision	:= 0;


BEGIN

select	coalesce(sum(vl_tributo),0),
	coalesce(sum(vl_base_calculo),0)
into STRICT	vl_tributo_w,
	vl_base_calculo_w
from	pls_prestador_tributo
where	trunc(dt_referencia,'month')	= trunc(dt_mes_referencia_p,'month')
and	nr_seq_prestador		= nr_seq_prestador_p;

if (ie_tipo_tributo_p	= 1) then
	vl_retorno_w	:= vl_tributo_w;
elsif (ie_tipo_tributo_p	= 2) then
	vl_retorno_w	:= vl_base_calculo_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
