CREATE OR REPLACE FUNCTION pls_obter_tx_ato_cooperado ( ie_tipo_ato_cooperado_p text, ie_tipo_item_mensalidade_p text, nr_seq_tipo_lanc_p bigint) RETURNS bigint AS $body$
DECLARE


tx_retorno_w		double precision;
tx_valor_ato_w		double precision;


BEGIN

select	a.tx_valor_ato
into STRICT	tx_valor_ato_w
from	pls_contab_ato_item c,
	pls_contab_ato_cooperado b,
	pls_contab_ato_regra a
where	a.ie_tipo_ato_cooperado	= ie_tipo_ato_cooperado_p
and	b.nr_sequencia		= a.nr_seq_ato_cooperado
and	b.nr_sequencia		= c.nr_seq_ato_cooperado
and	clock_timestamp()	between	coalesce(a.dt_inicio_vigencia, clock_timestamp()) and coalesce(a.dt_fim_vigencia, clock_timestamp())
and	c.ie_tipo_item_mensalidade = ie_tipo_item_mensalidade_p
and	((c.nr_seq_tipo_lanc = nr_seq_tipo_lanc_p) or (coalesce(nr_seq_tipo_lanc_p,0) = 0));

tx_retorno_w := tx_valor_ato_w/100;

return	tx_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
