CREATE OR REPLACE FUNCTION pls_obter_tx_reaj_relat_3030 ( nr_seq_contrato_p pls_contrato.nr_sequencia%type) RETURNS PLS_REAJUSTE.TX_REAJUSTE%TYPE AS $body$
DECLARE


dt_reajuste_w			pls_reajuste.dt_reajuste%type;
nr_seq_ultimo_reajuste_w	pls_reajuste.nr_sequencia%type;
tx_reajuste_w			pls_reajuste.tx_reajuste%type;
nr_seq_lote_deflator_w		pls_reajuste.nr_seq_lote_deflator%type;
tx_deflator_w			pls_reajuste.tx_deflator%type;


BEGIN

select	max(z.dt_reajuste)
into STRICT	dt_reajuste_w
from	pls_reajuste z
where	z.nr_seq_contrato	= nr_seq_contrato_p
and	z.ie_status		= '2';

select	max(nr_sequencia)
into STRICT	nr_seq_ultimo_reajuste_w
from	pls_reajuste
where	nr_seq_contrato	= nr_seq_contrato_p
and	dt_reajuste	= dt_reajuste_w;

if (nr_seq_ultimo_reajuste_w IS NOT NULL AND nr_seq_ultimo_reajuste_w::text <> '') then
	select	tx_reajuste,
		nr_seq_lote_deflator
	into STRICT	tx_reajuste_w,
		nr_seq_lote_deflator_w
	from	pls_reajuste
	where	nr_sequencia	= nr_seq_ultimo_reajuste_w;

	if (nr_seq_lote_deflator_w IS NOT NULL AND nr_seq_lote_deflator_w::text <> '') then
		select	tx_deflator
		into STRICT	tx_deflator_w
		from	pls_reajuste
		where	nr_sequencia = nr_seq_lote_deflator_w;

		tx_reajuste_w := (((1 * (1 + tx_reajuste_w / 100)) + (1 * (1 + tx_reajuste_w / 100)) * (-1 * tx_deflator_w / 100)) - 1) * 100;
	end if;
else
	tx_reajuste_w	:= 0;
end if;

return tx_reajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
