CREATE OR REPLACE FUNCTION pls_obter_tx_ultimo_reajuste ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, ie_tipo_contrato_p text, ie_deflator_p text default 'N') RETURNS PLS_REAJUSTE.TX_REAJUSTE%TYPE AS $body$
DECLARE


dt_reajuste_w			pls_reajuste.dt_reajuste%type;
nr_seq_ultimo_reajuste_w	pls_reajuste.nr_sequencia%type;
tx_reajuste_w			pls_reajuste.tx_reajuste%type;


BEGIN

if (ie_tipo_contrato_p = 'PF') then
	select	max(z.dt_reajuste)
	into STRICT	dt_reajuste_w
	from	pls_reajuste		z,
		pls_reajuste_tabela	x
	where	x.nr_seq_reajuste	= z.nr_sequencia
	and	x.nr_seq_contrato	= nr_seq_contrato_p
	and	z.ie_status		= '2';
elsif (ie_tipo_contrato_p = 'PJ') then
	select	max(z.dt_reajuste)
	into STRICT	dt_reajuste_w
	from	pls_reajuste z
	where	z.nr_seq_contrato	= nr_seq_contrato_p
	and	z.ie_status		= '2'
	and	((ie_deflator_p = 'S') or (coalesce(z.nr_seq_lote_deflator::text, '') = '' and	coalesce(z.tx_deflator::text, '') = ''));
end if;

select	max(nr_sequencia)
into STRICT	nr_seq_ultimo_reajuste_w
from	pls_reajuste
where	nr_seq_contrato	= nr_seq_contrato_p
and	dt_reajuste = dt_reajuste_w;

if (nr_seq_ultimo_reajuste_w IS NOT NULL AND nr_seq_ultimo_reajuste_w::text <> '') then
	select	CASE WHEN coalesce(tx_reajuste_correto,tx_reajuste)=0 THEN CASE WHEN ie_deflator_p='S' THEN tx_deflator  ELSE 0 END   ELSE coalesce(tx_reajuste_correto,tx_reajuste) END
	into STRICT	tx_reajuste_w
	from	pls_reajuste
	where	nr_sequencia = nr_seq_ultimo_reajuste_w;
else
	tx_reajuste_w	:= 0;
end if;

return tx_reajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
