CREATE OR REPLACE FUNCTION pls_obter_uf_prestador ( nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


sg_uf_w				pls_prestador_area.sg_estado%type;


BEGIN

select	coalesce(max(sg_estado),'X')
into STRICT	sg_uf_w
from	pls_prestador_area
where	nr_seq_prestador	= nr_seq_prestador_p;

if (sg_uf_w	= 'X') then
	select	max(b.ds_unidade_federacao)
	into STRICT	sg_uf_w
	from	sus_municipio		b,
		pls_prestador_area	a
	where	a.cd_municipio_ibge	= b.cd_municipio_ibge
	and	a.nr_seq_prestador	= nr_seq_prestador_p;
end if;

return	sg_uf_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
