CREATE OR REPLACE FUNCTION pls_obter_ultimo_aniversario ( dt_aniversario_p timestamp, dt_consistencia_p timestamp, ie_ultimo_aniversario_p text) RETURNS timestamp AS $body$
DECLARE


dt_aniversario_w	timestamp;


BEGIN

dt_aniversario_w	:= dt_aniversario_p;

while(dt_aniversario_w <= dt_consistencia_p) loop
	begin
	dt_aniversario_w	:= add_months(dt_aniversario_w,12);
	end;
end loop;

if (ie_ultimo_aniversario_p = 'S') then
	dt_aniversario_w	:= add_months(dt_aniversario_w,-12);
end if;

return	dt_aniversario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
