SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_ultimo_audit_analise ( nr_seq_analise_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_ordem_auditor_w		bigint;


BEGIN

select	max(nr_seq_ordem)
into STRICT	nr_seq_ordem_auditor_w
from 	pls_auditoria_conta_grupo
where	nr_seq_analise 	= nr_seq_analise_p
and	nr_seq_ordem	= (	SELECT  max(nr_seq_ordem)
				from 	pls_auditoria_conta_grupo
				where	nr_Seq_analise  = nr_seq_analise_p
				and	coalesce(ie_pre_analise,'N') = 'N'
				and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''));

if (coalesce(nr_seq_ordem_auditor_w,0) <> 0) then
	if (ie_opcao_p = 'A') then
		select	max(nm_auditor_atual)
		into STRICT	ds_retorno_w
		from	pls_auditoria_conta_grupo
		where	nr_seq_ordem = nr_seq_ordem_auditor_w
		and	nr_seq_analise	= nr_seq_analise_p;
	elsif (ie_opcao_p = 'D') then
		select	max(to_char(dt_liberacao,'dd/mm/yyyy'))
		into STRICT	ds_retorno_w
		from	pls_auditoria_conta_grupo
		where	nr_seq_ordem = nr_seq_ordem_auditor_w
		and	nr_seq_analise	= nr_seq_analise_p;
	elsif (ie_opcao_p = 'DH') then
		select	max(to_char(dt_liberacao,'dd/mm/yyyy hh24:mi:ss'))
		into STRICT	ds_retorno_w
		from	pls_auditoria_conta_grupo
		where	nr_seq_ordem = nr_seq_ordem_auditor_w
		and	nr_seq_analise	= nr_seq_analise_p;
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
