CREATE OR REPLACE FUNCTION pls_obter_unimed_estab ( cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_cgc_outorgante_w		varchar(14);
cd_cooperativa_w		varchar(10);


BEGIN

select	max(cd_cgc_outorgante)
into STRICT	cd_cgc_outorgante_w
from	pls_outorgante
where	cd_estabelecimento	= cd_estabelecimento_p;

if (coalesce(cd_cgc_outorgante_w::text, '') = '') then
	select	max(cd_cgc_outorgante)
	into STRICT	cd_cgc_outorgante_w
	from	pls_outorgante;

	if (cd_cgc_outorgante_w IS NOT NULL AND cd_cgc_outorgante_w::text <> '') then
		select	max(cd_cooperativa)
		into STRICT	cd_cooperativa_w
		from	pls_congenere
		where	cd_cgc		= cd_cgc_outorgante_w
		and	(cd_cooperativa IS NOT NULL AND cd_cooperativa::text <> '');
	end if;
else
	select	max(cd_cooperativa)
	into STRICT	cd_cooperativa_w
	from	pls_congenere
	where	cd_cgc			= cd_cgc_outorgante_w
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	(cd_cooperativa IS NOT NULL AND cd_cooperativa::text <> '');

	if (coalesce(cd_cooperativa_w::text, '') = '') then
		select	max(cd_cooperativa)
		into STRICT	cd_cooperativa_w
		from	pls_congenere
		where	cd_cgc		= cd_cgc_outorgante_w
		and	(cd_cooperativa IS NOT NULL AND cd_cooperativa::text <> '');
	end if;
end if;

return	cd_cooperativa_w;

end;
$body$
