CREATE OR REPLACE FUNCTION pls_obter_usuario_grupo_hist ( nr_seq_auditoria_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_grupo_w			bigint 	:= null;
nr_retorno_w			bigint	:= null;
nm_usuario_exec_w		varchar(15);
ie_auditor_grupo_w		varchar(1) := 'N';


BEGIN

begin
	select	nr_seq_grupo,
		nm_usuario_exec
	into STRICT	nr_seq_grupo_w,
		nm_usuario_exec_w
	from	pls_auditoria_grupo
	where	nr_sequencia = coalesce(pls_obter_grupo_analise_atual(nr_seq_auditoria_p),0);
exception
when others then
	nr_seq_grupo_w := null;
	nm_usuario_exec_w := null;
end;

if (nm_usuario_exec_w = nm_usuario_p) then
	nr_retorno_w := nr_seq_grupo_w;
elsif (coalesce(nm_usuario_exec_w,'X') = 'X') then
	ie_auditor_grupo_w := pls_obter_liberacao_auditor(nr_seq_auditoria_p,nm_usuario_p);

	if (ie_auditor_grupo_w = 'S') then
		nr_retorno_w := nr_seq_grupo_w;
	end if;
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
