CREATE OR REPLACE FUNCTION pls_obter_usu_hist_web ( nr_seq_requisicao_p bigint, nr_seq_contrato_p bigint, ie_tipo_contrato_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade:  Obter nome da operadora ou estipulante do histórico de solicitações pendentes 
---------------------------------------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [ ] Relatórios [ ] Outros: 
 ---------------------------------------------------------------------------------------------------------------------------------------------------- 
Pontos de atenção:  
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
	IE_TIPO_CONTRATO_P 
	 
	C - Contrato de Plano de Saúde 
	I - Intercâmbio 
*/					 
				 
qt_usuario_w		integer;	
nm_usuario_web_w	varchar(255);
cd_estabelecimento_w	bigint;

 

BEGIN 
 
select	count(*) 
into STRICT	qt_usuario_w 
from	usuario 
where 	nm_usuario = nm_usuario_p;
 
if (qt_usuario_w > 0) then 
	select	coalesce(cd_estabelecimento,1) 
	into STRICT	cd_estabelecimento_w 
	from	pls_requisicao 
	where 	nr_sequencia = nr_seq_requisicao_p;
 
	nm_usuario_web_w := pls_obter_nome_operadora_estab(cd_estabelecimento_w);
else 
	if (ie_tipo_contrato_p = 'C') then 
		nm_usuario_web_w := pls_obter_dados_contrato(nr_seq_contrato_p, 'E');
	else 
		nm_usuario_web_w := pls_obter_dados_contrato_inter(nr_seq_contrato_p, null, 'E');	
	end if;
end if;
 
return nm_usuario_web_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
