CREATE OR REPLACE FUNCTION pls_obter_validade_carteira ( cd_usuario_plano_p text, cd_estabelecimento_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_segurado_w		bigint;
dt_validade_cart_w		timestamp	:= '';


BEGIN

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_seq_segurado_w
from	pls_segurado a,
	pls_segurado_carteira b
where	a.nr_sequencia		= b.nr_seq_segurado
and	b.cd_usuario_plano	= cd_usuario_plano_p
and	b.cd_estabelecimento	= cd_estabelecimento_p;

if (nr_seq_segurado_w <> 0) then
	select	max(dt_validade_carteira)
	into STRICT	dt_validade_cart_w
	from	pls_segurado_carteira
	where	nr_seq_segurado   = nr_seq_segurado_w
	and	cd_usuario_plano  = cd_usuario_plano_p;
end if;

return	dt_validade_cart_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
