CREATE OR REPLACE FUNCTION pls_obter_valida_nm_comu_web (nm_usuario_estip_p text, nm_usuarios_p text) RETURNS varchar AS $body$
DECLARE


/*
verifica se o usuário pode visualizar o comunicado
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [   X] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

nm_usuario_estip_p  : Nome do usuário estipulante
nm_usuarios_p : nome dos usuários do comunicado (nm_usuario_prestador)
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nm_usuarios_w		pls_comunic_externa_web.nm_usuario_prestador%type;
nm_usuario_aux_w	varchar(255);
nm_usuario_w		varchar(255);


BEGIN
nm_usuarios_w 	 := trim(both nm_usuarios_p);
while(nm_usuarios_w IS NOT NULL AND nm_usuarios_w::text <> '') loop
if (position(',' in nm_usuarios_w) > 0) then
	nm_usuario_aux_w := trim(substr(nm_usuarios_w,0,(position(',' in nm_usuarios_w))));
	nm_usuario_w 	 := trim(substr(nm_usuarios_w,0,(position(',' in nm_usuarios_w)-1)));
else
	nm_usuario_aux_w := trim(both nm_usuarios_w);
	nm_usuario_w 	 := trim(both nm_usuarios_w);
end if;
	if (upper(nm_usuario_w) = upper(trim(both nm_usuario_estip_p))) then
		return 'S';
	end if;

	nm_usuarios_w := substr(nm_usuarios_w, length(nm_usuario_w) + 2, length(nm_usuarios_w));
end loop;

return	'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
