SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valid_req_guia_web ( nr_seq_requisicao_p bigint, nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter data de validade da senha na requisição
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w		varchar(10);
dt_validade_senha_w	timestamp;


BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	max(dt_validade_senha)
	into STRICT	dt_validade_senha_w
	from	pls_requisicao
	where	nr_sequencia = nr_seq_requisicao_p;


elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	max(dt_validade_senha)
	into STRICT	dt_validade_senha_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;
end if;

if (dt_validade_senha_w IS NOT NULL AND dt_validade_senha_w::text <> '') then
	ds_retorno_w := to_char(dt_validade_senha_w,'dd/mm/yyyy');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
