CREATE OR REPLACE FUNCTION pls_obter_valores_conta_proc ( nr_seq_conta_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

/*	VA	valor apresentado
	VL	valor valor liberado
	VD	valor diferen√ßa
	CO	custo operacional
	VM	valor material   */
vl_retorno_w		double precision;
vl_liberado_w		double precision;
vl_apresentado_w	double precision;
vl_custo_operacional_w  double precision;
vl_materiais_w		double precision;


BEGIN


select	coalesce(sum(vl_liberado ),0),
	coalesce(sum(vl_procedimento_imp),0),
	coalesce(sum(vl_custo_operacional),0),
	coalesce(sum(vl_materiais),0)
into STRICT	vl_liberado_w,
	vl_apresentado_w,
	vl_custo_operacional_w,
	vl_materiais_w
from	pls_conta_proc
where	nr_seq_conta 		= nr_seq_conta_p
and	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced 	= ie_origem_proced_p;

if (ie_tipo_p = 'VL') then
	vl_retorno_w	:= vl_liberado_w;
elsif (ie_tipo_p = 'VA') then
	vl_retorno_w	:= vl_apresentado_w;
elsif (ie_tipo_p = 'VD') then
	vl_retorno_w	:= ( vl_liberado_w - vl_apresentado_w);
elsif (ie_tipo_p = 'CO') then
	vl_retorno_w	:= vl_custo_operacional_w;
elsif (ie_tipo_p = 'VM') then
	vl_retorno_w	:= vl_materiais_w;
end if;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
