SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valores_conta_prot (nr_seq_protocolo_p bigint, ie_campo_p text) RETURNS bigint AS $body$
DECLARE


/* ------------- IE_CAMPO_P ----------------------
1 - Valor Coparticipação (VL_COPARTICIPACAO)
2 - Valor Total (VL_TOTAL)
3- Valor Apresentado (VL_COBRADO)
*/
vl_coparticipacao_proc_w		pls_conta_proc.vl_coparticipacao%type;
vl_total_proc_w				pls_conta_proc.vl_liberado%type;
vl_cobrado_proc_w			pls_conta_proc.vl_procedimento_imp%type;
vl_coparticipacao_mat_w			pls_conta_mat.vl_mat_copartic%type;
vl_total_mat_w				pls_conta_mat.vl_liberado%type;
vl_cobrado_mat_w			pls_conta_mat.vl_material_imp%type;
vl_coparticipacao_w			pls_conta_coparticipacao.vl_coparticipacao%type;
ds_retorno_w				double precision;


BEGIN

vl_coparticipacao_proc_w		:= 0;
vl_total_proc_w				:= 0;
vl_cobrado_proc_w			:= 0;
vl_coparticipacao_mat_w			:= 0;
vl_total_mat_w				:= 0;
vl_cobrado_mat_w			:= 0;
vl_coparticipacao_w			:= 0;
ds_retorno_w				:= 0;

if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	select 	sum(coalesce(a.vl_liberado,0)), -- Valor Total
		sum(coalesce(a.vl_procedimento_imp,0)) --Valor apresentado
	into STRICT	vl_total_proc_w,
		vl_cobrado_proc_w
	from   	pls_conta_proc a,
		pls_conta b
	where  	a.nr_seq_conta = b.nr_sequencia
	and    	b.nr_seq_protocolo = nr_seq_protocolo_p;

	select 	sum(coalesce(a.vl_liberado,0)), -- Valor Total
		sum(coalesce(a.vl_material_imp,0)) --Valor apresentado
	into STRICT	vl_total_mat_w,
		vl_cobrado_mat_w
	from   	pls_conta_mat a,
		pls_conta b
	where  	a.nr_seq_conta = b.nr_sequencia
	and    	b.nr_seq_protocolo = nr_seq_protocolo_p;

	select 	sum(coalesce(a.vl_coparticipacao,0))
	into STRICT	vl_coparticipacao_w
	from 	pls_conta_coparticipacao a,
		pls_conta b
	where 	a.nr_seq_conta = b.nr_sequencia
	and 	b.nr_seq_protocolo = nr_seq_protocolo_p;

	if (ie_campo_p = '1') then
			ds_retorno_w := vl_coparticipacao_w;
	elsif (ie_campo_p = '2') then
			ds_retorno_w := vl_total_proc_w + vl_total_mat_w;
	elsif (ie_campo_p = '3') then
			ds_retorno_w := vl_cobrado_proc_w + vl_cobrado_mat_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_valores_conta_prot (nr_seq_protocolo_p bigint, ie_campo_p text) FROM PUBLIC;

