SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valores_conta (nr_seq_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
/* 
'FAT' - Valores da conta para faturamento 
'PFAT' - Valores da conta pendente de faturamento 
'FFAT' - Valores da conta faturadas 
*/
 
 
vl_retorno_w		double precision := 0;


BEGIN 
 
if (ie_opcao_p = 'FAT') then		-- Vl para faturamento 
 
	select	coalesce(sum(coalesce(vl_medico,0) + coalesce(vl_custo_operacional,0) + coalesce(vl_materiais,0) + 
		coalesce(vl_lib_taxa_servico,0) + coalesce(vl_lib_taxa_co,0) + coalesce(vl_lib_taxa_material,0)),0) 
	into STRICT	vl_retorno_w 
	from	pls_conta_pos_estabelecido 
	where	nr_seq_conta	= nr_seq_conta_p 
	and	((ie_situacao	= 'A') or (coalesce(ie_situacao::text, '') = '')) 
	and	ie_status_faturamento	= 'L';
 
elsif (ie_opcao_p = 'PFAT') then		-- Vl pendende de faturamento 
 
	select	coalesce(sum(coalesce(vl_medico,0) + coalesce(vl_custo_operacional,0) + coalesce(vl_materiais,0) + 
		coalesce(vl_lib_taxa_servico,0) + coalesce(vl_lib_taxa_co,0) + coalesce(vl_lib_taxa_material,0)),0) 
	into STRICT	vl_retorno_w 
	from	pls_conta_pos_estabelecido 
	where	nr_seq_conta	= nr_seq_conta_p 
	and	((ie_situacao	= 'A') or (coalesce(ie_situacao::text, '') = '')) 
	and	coalesce(nr_seq_lote_fat::text, '') = '' 
	and	ie_status_faturamento	= 'L';
 
elsif (ie_opcao_p = 'FFAT') then		-- Vl faturado 
 
	select	coalesce(sum(coalesce(vl_medico,0) + coalesce(vl_custo_operacional,0) + coalesce(vl_materiais,0) + 
		coalesce(vl_lib_taxa_servico,0) + coalesce(vl_lib_taxa_co,0) + coalesce(vl_lib_taxa_material,0)),0) 
	into STRICT	vl_retorno_w 
	from	pls_conta_pos_estabelecido 
	where	nr_seq_conta	= nr_seq_conta_p 
	and	((ie_situacao	= 'A') or (coalesce(ie_situacao::text, '') = '')) 
	and	(nr_seq_lote_fat IS NOT NULL AND nr_seq_lote_fat::text <> '') 
	and	ie_status_faturamento	= 'L';
 
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
