SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valores_discussao ( nr_seq_discussao_p bigint, ie_opcao_p text ) RETURNS bigint AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Buscar as informações de valores da discussão (PLS_CONTESTACAO_DISCUSSAO)
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

VC - Valor contestação			VRO - Valor reconhecido original
VCO - Valor cobrado original		VRD - Valor reconhecido discussao
VG - Valor glosa			VN - Valor NDC
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/vl_retorno_w		double precision := 0;


BEGIN
if (ie_opcao_p = 'VC') then
	select	sum(coalesce(vl_contestado,0))
	into STRICT	vl_retorno_w
	from (SELECT	sum(coalesce(vl_contestado,0)) vl_contestado
		from	pls_discussao_proc
		where	nr_seq_discussao	= nr_seq_discussao_p
		
union all

		SELECT	sum(coalesce(vl_contestado,0)) vl_contestado
		from	pls_discussao_mat
		where	nr_seq_discussao	= nr_seq_discussao_p) alias7;

elsif (ie_opcao_p = 'VRO') then
	select	sum(coalesce(vl_reconh_orig,0))
	into STRICT	vl_retorno_w
	from (SELECT	sum(coalesce(vl_reconh_orig,0)) vl_reconh_orig
		from	pls_discussao_proc
		where	nr_seq_discussao	= nr_seq_discussao_p
		
union all

		SELECT	sum(coalesce(vl_reconh_orig,0)) vl_reconh_orig
		from	pls_discussao_mat
		where	nr_seq_discussao	= nr_seq_discussao_p) alias7;

elsif (ie_opcao_p = 'VCO') then
	select	sum(coalesce(vl_recurso,0))
	into STRICT	vl_retorno_w
	from (SELECT	sum(coalesce(vl_recurso,0)) vl_recurso
		from	pls_discussao_proc
		where	nr_seq_discussao	= nr_seq_discussao_p
		
union all

		SELECT	sum(coalesce(vl_recurso,0)) vl_recurso
		from	pls_discussao_mat
		where	nr_seq_discussao	= nr_seq_discussao_p) alias7;

elsif (ie_opcao_p = 'VRD') then
	select	sum(coalesce(vl_aceito,0))
	into STRICT	vl_retorno_w
	from (SELECT	sum(coalesce(vl_aceito,0)) vl_aceito
		from	pls_discussao_proc
		where	nr_seq_discussao	= nr_seq_discussao_p
		
union all

		SELECT	sum(coalesce(vl_aceito,0)) vl_aceito
		from	pls_discussao_mat
		where	nr_seq_discussao	= nr_seq_discussao_p) alias7;

elsif (ie_opcao_p = 'VG') then
	select	sum(coalesce(vl_negado,0))
	into STRICT	vl_retorno_w
	from (SELECT	sum(coalesce(vl_negado,0)) vl_negado
		from	pls_discussao_proc
		where	nr_seq_discussao	= nr_seq_discussao_p
		
union all

		SELECT	sum(coalesce(vl_negado,0)) vl_negado
		from	pls_discussao_mat
		where	nr_seq_discussao	= nr_seq_discussao_p) alias7;

elsif (ie_opcao_p = 'VN') then
	select	sum(coalesce(vl_ndc,0))
	into STRICT	vl_retorno_w
	from (SELECT	sum(coalesce(vl_ndc,0)) vl_ndc
		from	pls_discussao_proc
		where	nr_seq_discussao	= nr_seq_discussao_p
		
union all

		SELECT	sum(coalesce(vl_ndc,0)) vl_ndc
		from	pls_discussao_mat
		where	nr_seq_discussao	= nr_seq_discussao_p) alias7;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
