CREATE OR REPLACE FUNCTION pls_obter_valores_mensalidade (nr_seq_lote_p bigint, nr_seq_mensalidade_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*	'1' - Preço pré-estabelecido
	'2' - Taxa de inscrição
	'3' - Co-participação
	'4' - Reajuste - Variação de custo
	'5' - Reajuste - Mudança de faixa-etária
	'6' - Preço pós-estabelecido por custo operacional
	'7' - Preço pós-estabelecido por rateio
	'8' - Preço misto
	'17' - Taxa de emissão de boleto
	'A' - Lançamentos adicionais
	'C' - Total cancelado
	'TR' - Título - Total recebido	*/
vl_retorno_w			double precision	:= 0;
vl_item_w			double precision	:= 0;
vl_total_item_w			double precision	:= 0;
nr_seq_mensalidade_w		bigint;
nr_seq_mensalidade_seg_w	bigint;
vl_cancelado_W			double precision	:= 0;
vl_taxa_boleto_w		double precision	:= 0;
ie_passou_w			varchar(1) 	:= 'N';
vl_total_recebido_w		double precision	:= 0;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_mensalidade
	where (nr_seq_lote	= coalesce(nr_seq_lote_p,0)
	or	nr_sequencia	= coalesce(nr_seq_mensalidade_p,0));

c02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_mensalidade_segurado
	where	nr_seq_mensalidade	= nr_seq_mensalidade_w;


BEGIN

if (coalesce(nr_seq_lote_p,0) > 0) or (coalesce(nr_seq_mensalidade_p,0) > 0) then

	if (ie_opcao_p = 'TR') then
		select	((coalesce(sum(b.vl_recebido),0) + coalesce(sum(b.vl_juros),0) + coalesce(sum(b.vl_multa),0) + coalesce(sum(b.vl_rec_maior),0)) + coalesce(sum(b.vl_glosa),0))
		into STRICT	vl_total_recebido_w
		FROM pls_mensalidade c, titulo_receber a
LEFT OUTER JOIN titulo_receber_liq b ON (a.nr_titulo = b.nr_titulo)
WHERE a.nr_seq_mensalidade = c.nr_sequencia and coalesce(b.ie_lib_caixa, 'S') = 'S' and c.nr_sequencia = nr_seq_mensalidade_p;

		vl_retorno_w := vl_total_recebido_w;
	else
	if (ie_opcao_p = 'C') then
		select	sum(vl_mensalidade)
		into STRICT	vl_cancelado_W
		from	pls_mensalidade
		where (nr_seq_lote = coalesce(nr_seq_lote_p,0)
		or	nr_sequencia = coalesce(nr_seq_mensalidade_p,0))
		and	ie_cancelamento = 'C';

		vl_retorno_w	:= vl_cancelado_w;
	else
		vl_item_w		:= 0;
		vl_total_item_w		:= 0;
		open c01;
		loop
		fetch c01 into
			nr_seq_mensalidade_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			open c02;
			loop
			fetch c02 into
				nr_seq_mensalidade_seg_w;
			EXIT WHEN NOT FOUND; /* apply on c02 */
				if (ie_opcao_p = 'A') then
					select	coalesce(sum(vl_adicional),0)
					into STRICT	vl_item_w
					from	pls_mensalidade_seg_adic
					where	nr_seq_mensalidade_seg	= nr_seq_mensalidade_seg_w;
				else
					select	coalesce(sum(vl_item),0)
					into STRICT	vl_item_w
					from	pls_mensalidade_seg_item
					where	nr_seq_mensalidade_seg	= nr_seq_mensalidade_seg_w
					and	ie_tipo_item		= ie_opcao_p;

					if (ie_opcao_p = '17') then
						select	coalesce(sum(b.vl_taxa_boleto),0)
						into STRICT	vl_taxa_boleto_w
						from	pls_mensalidade_seg_item	b,
							pls_mensalidade_segurado	a
						where	a.nr_sequencia			= b.nr_seq_mensalidade_seg
						and	a.nr_sequencia			= nr_seq_mensalidade_seg_w;

						if (vl_taxa_boleto_w = 0) and (ie_passou_w = 'N')then
							ie_passou_w := 'S';
							select	coalesce(sum(b.vl_item),0)
							into STRICT	vl_taxa_boleto_w
							from	pls_mensalidade_seg_item	b,
								pls_mensalidade			a
							where	a.nr_sequencia			= b.nr_seq_mensalidade
							and	a.nr_sequencia			= nr_seq_mensalidade_w;
						end if;

						vl_item_w	:= coalesce(vl_item_w,0) + coalesce(vl_taxa_boleto_w,0);
					end if;
				end if;

				vl_total_item_w		:= vl_total_item_w + vl_item_w;
			end loop;
			close c02;
		end loop;
		close c01;

		vl_retorno_w	:= coalesce(vl_total_item_w,0);

	end if;
	end if;
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_valores_mensalidade (nr_seq_lote_p bigint, nr_seq_mensalidade_p bigint, ie_opcao_p text) FROM PUBLIC;

