SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valores_pagador ( nr_seq_pagador_p bigint, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


vl_titulo_atrasado_w		double precision;
vl_titulo_pendente_w		double precision;
ds_retorno_w			double precision;


BEGIN

select         	sum(a.vl_saldo_titulo)
into STRICT		vl_titulo_atrasado_w
from    	titulo_receber		a,
         	pls_mensalidade		b
where    	a.nr_seq_mensalidade	= b.nr_sequencia
and      	b.nr_seq_pagador    	= nr_seq_pagador_p
and      	a.ie_situacao in ('1','6')
and      	trunc(a.dt_pagamento_previsto,'dd')   <= trunc(clock_timestamp(),'dd');

select	        sum(a.vl_saldo_titulo)
into STRICT		vl_titulo_pendente_w
from     	titulo_receber 		a,
         	pls_mensalidade 	b
where    	a.nr_seq_mensalidade	= b.nr_sequencia
and      	b.nr_seq_pagador     	= nr_seq_pagador_p
and      	a.ie_situacao in ('1','6')
and      	trunc(a.dt_pagamento_previsto,'dd')   > trunc(clock_timestamp(),'dd');

vl_titulo_atrasado_w	:= coalesce(vl_titulo_atrasado_w,0);
vl_titulo_pendente_w	:= coalesce(vl_titulo_pendente_w,0);

if (ie_tipo_valor_p = 'A') then
	ds_retorno_w :=	vl_titulo_atrasado_w;
elsif (ie_tipo_valor_p = 'P') then
	ds_retorno_w :=	vl_titulo_pendente_w;
elsif (ie_tipo_valor_p = 'T') then
	ds_retorno_w := vl_titulo_atrasado_w + vl_titulo_pendente_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
