SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valores_ptu_servico ( nr_sequencia_p bigint, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE

/*	S - valor total servi√ßos
	F - valor total filmes
	C - valor total custo operacional
	T - valor total a700*/
vl_retorno_w		double precision;
vl_total_servicos_w	double precision;
vl_total_filme_w	double precision;
vl_total_co_w		double precision;


BEGIN

if (ie_tipo_valor_p = 'S') then

	select sum(coalesce(vl_procedimento,0) + coalesce(vl_adic_procedimento,0)) vl_total_servicos
	into STRICT	vl_retorno_w
	from	ptu_nota_servico a,
		ptu_nota_cobranca b
	where	a.nr_seq_nota_cobr = b.nr_sequencia
	and	b.nr_seq_serv_pre_pagto = nr_sequencia_p;

elsif (ie_tipo_valor_p = 'F') then

	select 	sum(coalesce(vl_filme,0) + coalesce(vl_adic_filme,0)) vl_total_filme
	into STRICT	vl_retorno_w
	from	ptu_nota_servico a,
		ptu_nota_cobranca b
	where	a.nr_seq_nota_cobr = b.nr_sequencia
	and	b.nr_seq_serv_pre_pagto = nr_sequencia_p;

elsif (ie_tipo_valor_p = 'C') then

	select	sum(coalesce(vl_custo_operacional,0) + coalesce(vl_adic_co,0)) vl_total_co
	into STRICT	vl_retorno_w
	from	ptu_nota_servico a,
		ptu_nota_cobranca b
	where	a.nr_seq_nota_cobr = b.nr_sequencia
	and	b.nr_seq_serv_pre_pagto = nr_sequencia_p;

elsif (ie_tipo_valor_p = 'T') then

	select	sum(coalesce(vl_procedimento,0) + coalesce(vl_adic_procedimento,0)),
		sum(coalesce(vl_filme,0) + coalesce(vl_adic_filme,0)),
		sum(coalesce(vl_custo_operacional,0) + coalesce(vl_adic_co,0))
	into STRICT	vl_total_servicos_w,
		vl_total_filme_w,
		vl_total_co_w
	from	ptu_nota_servico a,
		ptu_nota_cobranca b
	where	a.nr_seq_nota_cobr = b.nr_sequencia
	and	b.nr_seq_serv_pre_pagto = nr_sequencia_p;

	vl_retorno_w := vl_total_servicos_w + vl_total_filme_w + vl_total_co_w;
end if;

if (coalesce(vl_retorno_w::text, '') = '') then
	vl_retorno_w := 0;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
