CREATE OR REPLACE FUNCTION pls_obter_valores_recibo ( nr_seq_lote_p bigint, nr_seq_prestador_p bigint, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision := 0;


BEGIN

if (ie_tipo_valor_p	= 'D') then
	select	sum(e.vl_imposto)
	into STRICT	vl_retorno_w
	FROM pls_pagamento_item c, pls_pagamento_prestador b, pls_lote_pagamento a, pls_pag_prest_vencimento d
LEFT OUTER JOIN pls_pag_prest_venc_trib e ON (d.nr_sequencia = e.nr_seq_vencimento)
WHERE a.nr_sequencia			= b.nr_seq_lote and b.nr_sequencia			= c.nr_seq_pagamento and b.nr_sequencia			= d.nr_seq_pag_prestador  and a.nr_sequencia			= nr_seq_lote_p and b.nr_seq_prestador		= nr_seq_prestador_p;
elsif (ie_tipo_valor_p	= 'C') then
	select	sum(c.vl_item)
	into STRICT	vl_retorno_w
	from	pls_pagamento_item	c,
		pls_pagamento_prestador	b,
		pls_lote_pagamento	a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	b.nr_sequencia		= c.nr_seq_pagamento
	and	a.nr_sequencia		= nr_seq_lote_p
	and	b.nr_seq_prestador	= nr_seq_prestador_p;
elsif (ie_tipo_valor_p	= 'L') then
	select	sum(d.vl_liquido)
	into STRICT	vl_retorno_w
	from	pls_pag_prest_vencimento	d,
		pls_pagamento_item		c,
		pls_pagamento_prestador		b,
		pls_lote_pagamento		a
	where	a.nr_sequencia			= b.nr_seq_lote
	and	b.nr_sequencia			= c.nr_seq_pagamento
	and	b.nr_sequencia			= d.nr_seq_pag_prestador
	and	a.nr_sequencia			= nr_seq_lote_p
	and	b.nr_seq_prestador		= nr_seq_prestador_p;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
