CREATE OR REPLACE FUNCTION pls_obter_valor_apropriacao ( nr_seq_segurado_p bigint, dt_reajuste_p timestamp, nr_seq_preco_p bigint, nr_seq_centro_apropriacao_p bigint, vl_item_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_tabela_w			pls_tabela_preco.nr_sequencia%type;
nr_seq_plano_w			pls_plano.nr_sequencia%type;
nr_seq_classif_dependencia_w	pls_classif_dependencia.nr_sequencia%type;
nr_seq_regra_apropriacao_w	pls_regra_apropriacao.nr_sequencia%type;
vl_apropriacao_w		double precision;
tx_apropriacao_w		pls_regra_apropriacao_item.tx_apropriacao%type;
vl_total_apropriacao_w		double precision;
vl_reajuste_w			double precision;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_apropriacao
	where	nr_seq_plano			= nr_seq_plano_w
	and	nr_seq_tabela			= nr_seq_tabela_w
	and	((nr_seq_classif_dependencia	= nr_seq_classif_dependencia_w) or (coalesce(nr_seq_classif_dependencia::text, '') = ''))
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	dt_reajuste_p between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_reajuste_p)
	order by coalesce(nr_seq_classif_dependencia,0);


BEGIN

select	nr_seq_tabela,
	nr_seq_plano,
	nr_seq_classif_dependencia
into STRICT	nr_seq_tabela_w,
	nr_seq_plano_w,
	nr_seq_classif_dependencia_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

open C01;
loop
fetch C01 into
	nr_seq_regra_apropriacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

vl_reajuste_w := 0;

if (nr_seq_regra_apropriacao_w IS NOT NULL AND nr_seq_regra_apropriacao_w::text <> '') then
	select	sum(vl_apropriacao)
	into STRICT	vl_total_apropriacao_w
	from	pls_regra_apropriacao_item
	where	nr_seq_apropriacao	= nr_seq_regra_apropriacao_w
	and	nr_seq_preco		= nr_seq_preco_p;

	select	vl_apropriacao,
		tx_apropriacao
	into STRICT	vl_apropriacao_w,
		tx_apropriacao_w
	from	pls_regra_apropriacao_item
	where	nr_seq_apropriacao	  = nr_seq_regra_apropriacao_w
	and	nr_seq_preco		  = nr_seq_preco_p
	and	nr_seq_centro_apropriacao = nr_seq_centro_apropriacao_p;

	if (vl_apropriacao_w IS NOT NULL AND vl_apropriacao_w::text <> '') then
		vl_reajuste_w 		:= (vl_apropriacao_w * vl_item_p)/vl_total_apropriacao_w;
	else
		vl_reajuste_w		:= vl_item_p * (coalesce(tx_apropriacao_w,0) / 100);
	end if;
end if;

return	vl_reajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
