CREATE OR REPLACE FUNCTION pls_obter_valor_censo ( ie_dimensao_p text, ie_dimensao_secundaria_p text, dt_mes_referencia_p timestamp, ie_tipo_segurado_p text, ie_opcao_p text, nr_seq_referencia_p text, ie_benef_sca_p text) RETURNS bigint AS $body$
DECLARE

/* ie_ocpao_p
	D - Detalhado (por dimensão)
	DI - Detalhado (pela sub-dimensão)
	T - Total
*/
vl_retorno_w			numeric(30);


BEGIN
if (ie_benef_sca_p = 'S') then
	if (ie_opcao_p = 'D') then
		select	count(1)
		into STRICT	vl_retorno_w
		from	w_pls_censo_geral a
		where	dt_mes_referencia	= dt_mes_referencia_p
		and	ie_dimensao		= ie_dimensao_p
		and	ie_tipo_segurado	= coalesce(ie_tipo_segurado_p,ie_tipo_segurado)
		and	exists (SELECT count(1) from pls_sca_vinculo h where h.nr_seq_segurado = a.nr_seq_segurado );
	elsif (ie_opcao_p = 'DI') then
		select	count(1)
		into STRICT	vl_retorno_w
		from	w_pls_censo_geral a
		where	dt_mes_referencia	= dt_mes_referencia_p
		and	ie_dimensao		= ie_dimensao_p
		and	ie_dimensao_secundaria	= ie_dimensao_secundaria_p
		and	ie_tipo_segurado	= coalesce(ie_tipo_segurado_p,ie_tipo_segurado)
		and	nr_seq_referencia	= nr_seq_referencia_p
		and	exists (SELECT count(1) from pls_sca_vinculo h where h.nr_seq_segurado = a.nr_seq_segurado );
	elsif (ie_opcao_p = 'T') then
		select	count(1)
		into STRICT	vl_retorno_w
		from	w_pls_censo_geral a
		where	dt_mes_referencia	= dt_mes_referencia_p
		and	ie_tipo_segurado	= coalesce(ie_tipo_segurado_p,ie_tipo_segurado)
		and	(ie_dimensao IS NOT NULL AND ie_dimensao::text <> '')
		and	exists (SELECT count(1) from pls_sca_vinculo h where h.nr_seq_segurado = a.nr_seq_segurado );
	end if;

else
	if (ie_opcao_p = 'D') then
		select	count(1)
		into STRICT	vl_retorno_w
		from	w_pls_censo_geral
		where	dt_mes_referencia	= dt_mes_referencia_p
		and	ie_dimensao		= ie_dimensao_p
		and	ie_tipo_segurado	= coalesce(ie_tipo_segurado_p,ie_tipo_segurado);
	elsif (ie_opcao_p = 'DI') then
		select	count(1)
		into STRICT	vl_retorno_w
		from	w_pls_censo_geral
		where	dt_mes_referencia	= dt_mes_referencia_p
		and	ie_dimensao		= ie_dimensao_p
		and	ie_dimensao_secundaria	= ie_dimensao_secundaria_p
		and	ie_tipo_segurado	= coalesce(ie_tipo_segurado_p,ie_tipo_segurado)
		and	nr_seq_referencia	= nr_seq_referencia_p;
	elsif (ie_opcao_p = 'T') then
		select	count(1)
		into STRICT	vl_retorno_w
		from	w_pls_censo_geral
		where	dt_mes_referencia	= dt_mes_referencia_p
		and	ie_tipo_segurado	= coalesce(ie_tipo_segurado_p,ie_tipo_segurado)
		and	(ie_dimensao IS NOT NULL AND ie_dimensao::text <> '');
	end if;
end if;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
