SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_contrato (nr_seq_contrato_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision	:= 0;
vl_preco_w			double precision	:= 0;
vl_contrato_w			double precision	:= 0;
nr_seq_segurado_w		bigint;
nr_seq_segurado_preco_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_rescisao::text, '') = '';


BEGIN

if (coalesce(nr_seq_contrato_p,0) > 0) then

	open c01;
	loop
	fetch c01 into	nr_seq_segurado_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_segurado_preco_w
		from	pls_segurado_preco
		where	nr_seq_segurado	= nr_seq_segurado_w
		and	dt_reajuste	=
					(SELECT	max(dt_reajuste)
					from	pls_segurado_preco
					where	nr_seq_segurado	= nr_seq_segurado_w);

		if (nr_seq_segurado_preco_w > 0) then

			select	coalesce(vl_preco_atual,0) - coalesce(vl_desconto,0)
			into STRICT	vl_preco_w
			from	pls_segurado_preco
			where	nr_sequencia	= nr_seq_segurado_preco_w;

			vl_contrato_w	:= vl_contrato_w + vl_preco_w;

		end if;

	end loop;
	close c01;

	if (ie_opcao_p = 'C') then
		vl_retorno_w	:= vl_contrato_w;
	end if;

end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
