SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_copartic_mes ( dt_mes_referencia_p timestamp, ie_tipo_p bigint) RETURNS bigint AS $body$
DECLARE


/* IE_TIPO_P
	1 - Contas médicas
	2 - Reembolso
	3 - Contas médicas + Reembolso
*/
vl_retorno_w			double precision	:= 0;
vl_conta_medica_w		double precision	:= 0;
vl_reembolso_w			double precision	:= 0;


BEGIN

if (ie_tipo_p in (1,3)) then
	select	coalesce(sum(d.vl_coparticipacao),0)
	into STRICT	vl_conta_medica_w
	from 	pls_lote_mensalidade		g,
		pls_mensalidade			f,
		pls_mensalidade_segurado 	e,
		pls_conta_coparticipacao 	d,
		pls_conta_proc   		c,
		pls_conta			b,
		pls_protocolo_conta		a
	where 	a.nr_sequencia			= b.nr_seq_protocolo
	and	b.nr_sequencia  		= c.nr_seq_conta
	and 	c.nr_sequencia  		= d.nr_seq_conta_proc
	and 	d.nr_seq_mensalidade_seg	= e.nr_sequencia
	and	e.nr_seq_mensalidade		= f.nr_sequencia
	and	f.nr_seq_lote			= g.nr_sequencia
	and	a.ie_status			= 3
	and	a.ie_tipo_protocolo		= 'C'
	and 	trunc(g.dt_mesano_referencia, 'month') = trunc(dt_mes_referencia_p, 'month');
end if;
if (ie_tipo_p in (2,3)) then
	select	coalesce(sum(d.vl_coparticipacao),0)
	into STRICT	vl_reembolso_w
	from 	pls_conta_coparticipacao 	d,
		pls_conta_proc   		c,
		pls_conta			b,
		pls_protocolo_conta		a
	where 	a.nr_sequencia			= b.nr_seq_protocolo
	and	b.nr_sequencia  		= c.nr_seq_conta
	and 	c.nr_sequencia  		= d.nr_seq_conta_proc
	and	a.ie_status			= 3
	and	a.ie_tipo_protocolo		= 'R'
	and 	trunc(a.dt_mes_competencia, 'month') = trunc(dt_mes_referencia_p, 'month');
end if;

if (ie_tipo_p	= 1) then
	vl_retorno_w	:= vl_conta_medica_w;
elsif (ie_tipo_p	= 2) then
	vl_retorno_w	:= vl_reembolso_w;
elsif (ie_tipo_p	= 3) then
	vl_retorno_w	:= vl_conta_medica_w + vl_reembolso_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_valor_copartic_mes ( dt_mes_referencia_p timestamp, ie_tipo_p bigint) FROM PUBLIC;

