CREATE OR REPLACE FUNCTION pls_obter_valor_despesa_co ( cd_ano_p bigint, dt_mes_p timestamp) RETURNS bigint AS $body$
DECLARE



	ds_retorno_w		double precision;


BEGIN

select	coalesce(sum(b.vl_total_beneficiario),0) vl_total_beneficiario
into STRICT	ds_retorno_w
FROM mes_v a
LEFT OUTER JOIN pls_protocolo_conta c ON (a.dt_mes = c.dt_mes_competencia)
, pls_plano d
LEFT OUTER JOIN pls_conta b ON (d.nr_sequencia = b.nr_seq_plano)
WHERE to_char(a.dt_mes, 'YYYY') = cd_ano_p   and d.ie_preco = 3 and a.dt_mes = dt_mes_p group by	a.dt_mes;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
