CREATE OR REPLACE FUNCTION pls_obter_valor_despesa_mes ( dt_mes_p timestamp, nr_seq_contrato_p bigint, ie_tipo_valor_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w			double precision;


BEGIN
if (ie_tipo_valor_p = 'A') then
	if (coalesce(nr_seq_contrato_p,0) = 0) then
		select	coalesce(sum(b.vl_total),0) vl_despesa
		into STRICT	vl_retorno_w
		from	pls_conta		b,
			pls_protocolo_conta	a
		where	trunc(a.dt_mes_competencia, 'Month') = trunc(dt_mes_p,'Month')
		and	b.nr_seq_protocolo	= a.nr_sequencia;
	elsif (coalesce(nr_seq_contrato_p,0) > 0) then
		select	coalesce(sum(b.vl_total),0) vl_despesa
		into STRICT	vl_retorno_w
		from	pls_segurado		c,
			pls_conta		b,
			pls_protocolo_conta	a
		where	trunc(a.dt_mes_competencia, 'Month') = trunc(dt_mes_p,'Month')
		and	b.nr_seq_protocolo	= a.nr_sequencia
		and	b.nr_seq_segurado	= c.nr_sequencia
		and	c.nr_seq_contrato	= nr_seq_contrato_p;
	end if;
elsif (ie_tipo_valor_p	= 'AC') then
	if (coalesce(nr_seq_contrato_p,0) = 0) then
		select	coalesce(sum(b.vl_total),0) vl_despesa
		into STRICT	vl_retorno_w
		from	pls_conta		b,
			pls_protocolo_conta	a
		where	a.dt_mes_competencia between trunc(dt_mes_p,'yyyy') and last_day(dt_mes_p)
		and	b.nr_seq_protocolo	= a.nr_sequencia;
	elsif (coalesce(nr_seq_contrato_p,0) > 0) then
		select	coalesce(sum(b.vl_total),0) vl_despesa
		into STRICT	vl_retorno_w
		from	pls_segurado		c,
			pls_conta		b,
			pls_protocolo_conta	a
		where	a.dt_mes_competencia between trunc(dt_mes_p,'yyyy') and last_day(dt_mes_p)
		and	b.nr_seq_protocolo	= a.nr_sequencia
		and	b.nr_seq_segurado	= c.nr_sequencia
		and	c.nr_seq_contrato	= nr_seq_contrato_p;
	end if;
end if;
return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
