SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_valor_discussao_item ( nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
nr_seq_item_w		bigint;


BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	select	max(b.nr_sequencia)
	into STRICT	nr_seq_item_w
	from	PLS_CONTESTACAO_DISCUSSAO	b,
		pls_contestacao			a
	where	b.nr_seq_contestacao	= a.nr_sequencia
	and	a.nr_seq_conta		= nr_seq_conta_p;

	if (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') then
		vl_retorno_w	:= pls_obter_valores_discussao(nr_seq_item_w,'VN');
	end if;
elsif (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_item_w
	from	PLS_DISCUSSAO_PROC
	where	NR_SEQ_CONTA_PROC	= nr_seq_conta_proc_p;

	if (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') then
		select	VL_NDC
		into STRICT	vl_retorno_w
		from	PLS_DISCUSSAO_PROC
		where	nr_sequencia	= nr_seq_item_w;
	end if;
elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_item_w
	from	PLS_DISCUSSAO_MAT
	where	NR_SEQ_CONTA_MAT	= nr_seq_conta_mat_p;

	if (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') then
		select	VL_NDC
		into STRICT	vl_retorno_w
		from	PLS_DISCUSSAO_MAT
		where	nr_sequencia	= nr_seq_item_w;
	end if;
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
