CREATE OR REPLACE FUNCTION pls_obter_valor_estrat_aciplan ( nr_seq_item_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE

/*
P - Procedimento
T - Taxas
D - Diárias
 MA- Materiais Médicos
PS - Psicologa
F - Fisioterapia
H -  Clinica de Medicina Hiperbárica
O - Clinica de Odontologia
OF -  Clinica de Oftalmologia
LD - Laboratorio e Diagnostico de Imagem
FA- Farmácias
*/
cd_tipo_prestador_w		varchar(10);
ie_tipo_despesa_w		varchar(10);
ds_retorno_w			varchar(5);
ie_tipo_despesa_ww		varchar(10);


BEGIN
if (ie_tipo_item_p = 'P') then
	select	coalesce(d.cd_tipo_prestador, 'X'),
		a.ie_tipo_despesa
	into STRICT	cd_tipo_prestador_w,
		ie_tipo_despesa_w
	FROM pls_conta_proc a, pls_prestador c
LEFT OUTER JOIN pls_tipo_prestador d ON (c.nr_seq_tipo_prestador = d.nr_sequencia)
, pls_conta b
LEFT OUTER JOIN pls_prestador c ON (b.nr_seq_prestador_exec = c.nr_sequencia)
WHERE a.nr_seq_conta		= b.nr_sequencia and a.nr_sequencia		= nr_seq_item_p;
end if;

if	((cd_tipo_prestador_w = '2') or (cd_tipo_prestador_w = '3')) then
	ds_retorno_w	:= 'LD';
elsif (cd_tipo_prestador_w = '5') then
	ds_retorno_w	:= 'FA';
elsif (cd_tipo_prestador_w = '6') then
	ds_retorno_w	:= 'F';
elsif (cd_tipo_prestador_w = '8') then
	ds_retorno_W	:= 'OF';
elsif (cd_tipo_prestador_w = '9') then
	ds_retorno_w 	:= 'O';
elsif (cd_tipo_prestador_w = '12') then
	ds_retorno_w	:= 'H';
elsif (cd_tipo_prestador_w = '13') then
	ds_retorno_w	:= 'PS';
elsif (ie_tipo_despesa_w  = '1')then
	ds_retorno_w	:= 'P';
elsif (ie_tipo_despesa_w  = '2') then
	ds_retorno_w	:= 'T';
elsif (ie_tipo_despesa_w  = '3') then
	ds_retorno_w	:= 'D';
end if;



return ds_retorno_w;

end;
$body$
