CREATE OR REPLACE FUNCTION pls_obter_valor_franquia (nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


vl_franquia_w		double precision	:= 0;
qt_registros_w		smallint;


BEGIN


select	count(*)
into STRICT	qt_registros_w
from 	pls_franquia_guia
where	nr_seq_guia = nr_seq_guia_p;


if (qt_registros_w > 0) then
	select 	sum(vl_franquia)
	into STRICT	vl_franquia_w
	from 	pls_franquia_guia
	where	nr_seq_guia = nr_seq_guia_p;
end if;

return vl_franquia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
