CREATE OR REPLACE FUNCTION pls_obter_valor_glosa_calc ( nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


vl_procedimento_w	pls_conta_proc.vl_procedimento%type;
vl_lib_proc_w		pls_conta_proc.vl_liberado%type;
vl_material_w		pls_conta_mat.vl_material%type;
vl_lib_mat_w		pls_conta_mat.vl_liberado%type;
vl_retorno_w		double precision := 0;


BEGIN

if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	select	coalesce(vl_procedimento,0),
		coalesce(vl_liberado,0)
	into STRICT	vl_procedimento_w,
		vl_lib_proc_w
	from	pls_conta_proc
	where	nr_sequencia = nr_seq_conta_proc_p;

	vl_retorno_w := vl_procedimento_w - vl_lib_proc_w;

	if (vl_procedimento_w <= vl_lib_proc_w) then
		vl_retorno_w := 0;
	end if;
elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
	select	coalesce(vl_material,0),
		coalesce(vl_liberado,0)
	into STRICT	vl_material_w,
		vl_lib_mat_w
	from	pls_conta_mat
	where	nr_sequencia = nr_seq_conta_mat_p;

	vl_retorno_w := vl_material_w - vl_lib_mat_w;

	if (vl_material_w <= vl_lib_mat_w) then
		vl_retorno_w := 0;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
